    print(2 * max(x, y) - (x != y))

for _ in range(int(input())):
    (x, y) = map(int, input().split())
    if x == y:
        print(x + y)
    else:
        print(x + y + abs(x - y) - 1)

t = int(input())
for i in range(t):
    (a, b) = map(int, input().split())
    if a == 0 and b == 0:
        print('0')
    elif a == 0 or b == 0:
        print(2 * max(a, b) - 1)
    elif abs(a - b) <= 1:
        print(a + b)
    else:
        print(2 * max(a, b) - 1)
