MOD = 1000000007

def add(x, y):
    return (x + y) % MOD

def sub(x, y):
    return (x - y + MOD) % MOD

def mul(x, y):
    return x * y % MOD

def gcd(x, y):
    if y == 0:
        return x
    return gcd(y, x % y)

def lcm(x, y):
    return x * y // gcd(x, y)

def power(x, y):
    res = 1
    x %= MOD
    while y != 0:
        if y & 1:
            res = mul(res, x)
        y >>= 1
        x = mul(x, x)
    return res

def mod_inv(n):
    return power(n, MOD - 2)

def prob(p, q):
    return mul(p, power(q, MOD - 2))

