    sumn = 0
    p = []
    n = []
    i = 0
    j = 0
    ans = []
    for x in range(num):
        diff = a[x] - b[x]
        if diff > 0:
            p.append([diff, x])
            sump = sump + diff
        elif diff < 0:
            n.append([diff, x])
            sumn = sumn - diff
    if sump != sumn:
        return -1
    sumn = 0
    while True:
        if p[i][0] == 0:
            i = i + 1
        if n[j][0] == 0:
            j = j + 1
