    (plus, minus) = (0, 0)
    for i in range(len(a)):
        if a[i] > b[i]:
            minus += a[i] - b[i]
        elif a[i] < b[i]:
            plus += b[i] - a[i]
    if plus == minus:
        return plus
    return -1

