def Dominant_Piranha(arr):
    n = len(arr)
    Max = max(arr)
    if arr[n - 1] == Max and arr[n - 2] < Max:
        return n
    for i in range(n - 2, -1, -1):
        if arr[i] == Max:
            if arr[i - 1] < Max or arr[i + 1] < Max:
                return i + 1
    else:
        return -1
t = int(input())
final = []
for i in range(t):
    n = int(input())
    arr = list((int(x) for x in input().split()))
    res = Dominant_Piranha(arr)
    final.append(res)
for i in final:
    print(i)

