def answer(a, b, c, d):
    if a <= b:
        return b
    if c <= d:
        return -1
    initial_sleep = b
    each_cycle = c - d
    sleep_req_rem = a - initial_sleep
    if sleep_req_rem % each_cycle == 0:
        num_cycles = sleep_req_rem // each_cycle
    else:
        num_cycles = sleep_req_rem // each_cycle + 1
    return initial_sleep + c * num_cycles

