def Rsolve(s, i, j):
    if i == 0 and j == len(s):
        return 'RB' * int(len(s) / 2) + 'R' * int(len(s) % 2 == 1)
    elif i == 0:
        temp = s[j]
        if temp == 'R':
            if j % 2:
                return 'BR' * int(j / 2) + 'B'
            else:
                return 'RB' * int(j / 2)
        elif j % 2:
            return 'RB' * int(j / 2) + 'R'
        else:
            return 'BR' * int(j / 2)
    elif j == len(s):
        temp = s[i - 1]
        if temp == 'R':
            return 'BR' * int((j - i) / 2) + 'B' * int((j - i) % 2)
        else:
            return 'RB' * int((j - i) / 2) + 'R' * int((j - i) % 2)
    else:
        temp = s[i - 1]
        if temp == 'R':
            return 'BR' * int((j - i) / 2) + 'B' * int((j - i) % 2)
        else:
            return 'RB' * int((j - i) / 2) + 'R' * int((j - i) % 2)

def solve(s):
    i = 0
    ss = ''
    while i < len(s):
        if s[i] == '?':
            temp = i
            while i < len(s) and s[i] == '?':
                i = i + 1
            ss = ss + Rsolve(s, temp, i)
        else:
            ss = ss + s[i]
            i = i + 1
    return ss
