def solve(permu):
    if len(permu) < 3:
        return (-1, -1, -1)
    start = 0
    end = len(permu) - 1
    n = len(permu)
    while end - start > 1:
        if permu[start] == n:
            start += 1
            n -= 1
            continue
        if permu[end] == n:
            end -= 1
            n -= 1
            continue
        for i in range(start, end + 1):
            if permu[i] == n:
                return (i - 1, i, i + 1)
    return (-1, -1, -1)
