import sys

def parse_next_line():
    test_str = sys.stdin.readline()
    (x_str, y_str) = test_str.split(' ')
    x = int(x_str)
    y = int(y_str)
    return (x, y)

def check(a1, a2, b1, b2):
    surf = a1 * b1 + a2 * b2
    side = math.sqrt(surf)
    if a1 != side and b1 != side:
        return False
    if a2 != side and b2 != side:
        return False
    if a1 != side:
        return check(b1, a2, a1, b2)
    if a2 != side:
        return check(a1, b2, b1, a2)
    assert a1 == side
    assert a2 == side
    return b1 + b2 == side

def main(_):
    num_inputs = int(sys.stdin.readline())
    for _ in range(num_inputs):
        (a1, b1) = parse_next_line()
        (a2, b2) = parse_next_line()
        if check(a1, a2, b1, b2):
            print('Yes')
        else:
            print('No')
main(sys.argv[1:])

