def binarySearch(a, x):
    i = bisect.bisect_left(a, x)
    if i != len(a) and a[i] == x:
        return i
    else:
        return -1

def lowerBound(a, x):
    i = bisect.bisect_left(a, x)
    if i:
        return i - 1
    else:
        return -1

def upperBound(a, x):
    i = bisect.bisect_right(a, x)
    if i != len(a) + 1 and a[i - 1] == x:
        return i - 1
    else:
        return -1

def primesInRange(n):
    ans = []
    prime = [True for i in range(n + 1)]
    p = 2
    while p * p <= n:
        if prime[p] == True:
            for i in range(p * p, n + 1, p):
                prime[i] = False
        p += 1
    for p in range(2, n + 1):
        if prime[p]:
            ans.append(p)
    return ans
