t = int(input())
while t:
    t -= 1
    a = [[9, 2, 4], [3, 5, 7], [6, 8, 1]]
    n = int(input())
    if n == 1:
        print(1)
        continue
    if n == 2:
        print(-1)
        continue
    for i in range(3, n):
        for j in range(i):
            a[j].append(i * i + 1 + j * 2)
        sub = []
        for j in range(i):
            sub.append(i * i + 2 + j * 2)
        sub.append(a[0][0])
        a[0][0] = (i + 1) ** 2
        a.append(sub)
    for i in range(n):
        print(' '.join([str(I) for I in a[i]]))
