class Solution:

    def median(self, nums):
        nums.sort()
        req = len(nums) // 2
        return abs(nums[req] - nums[req - 1])
for _ in range(int(input())):
    n = int(input())
    res = Solution().median(list(map(int, input().split())))
    print(res)

def find_absme(n, a):
    a = sorted(a)
    return abs(a[n] - a[n - 1])
