def pairable(n, sorted_list):
    mod1count = sum(map(lambda x: x % 2 == 1, sorted_list))
    mod2count = n - mod1count
    if mod1count % 2 == 0 and mod2count % 2 == 0:
        return True
    previous = -1
    for i in sorted_list:
        if previous == -1:
            previous = i
            continue
        if i - previous == 1:
            return True
        previous = i
    return False
