def fnde(x, arr):
    index = 0
    n = len(arr)
    while arr[index] % x == 0:
        index += 1
        if index >= n:
            return -1
    return index

def lnde(x, arr):
    n = len(arr)
    index = n - 1
    while arr[index] % x == 0:
        index -= 1
        if index < 0:
            return -1
    return index
