    a = sorted(list(map(int, input().split())), reverse=True)
    for i in range(n):
        if i % 2 == 0 and a[i] % 2 == 0:
            sc += a[i]
        elif i % 2 != 0 and a[i] % 2 != 0:
            sl += a[i]
    if sc > sl:
        print('Alice')
    elif sc < sl:
        print('Bob')
    else:
        print('Tie')
