    b -= ns.count('1')
    if a < 0 or b < 0:
        return -1
    for i in range(n):
        cpi = n - i - 1
        if ns[i] != '?':
            if ns[cpi] == '?':
                ns[cpi] = ns[i]
                if ns[i] == '0' and a > 0:
                    a -= 1
                elif ns[i] == '1' and b > 0:
                    b -= 1
                else:
                    return -1
            elif ns[cpi] != ns[i]:
                return -1
    for i in range(n // 2):
        cpi = n - i - 1
        if ns[i] == ns[cpi] == '?':
            if a > 1:
                ns[i] = ns[cpi] = '0'
                a -= 2
            elif b > 1:
                ns[i] = ns[cpi] = '1'
                b -= 2
