    pal = list(pal)
    for k in range(len(pal) // 2):
        pair = [pal[k], pal[-k - 1]]
        if '?' not in pair and pair[0] != pair[1]:
            return -1
        if '0' in pair:
            (pal[k], pal[-k - 1]) = ('0', '0')
            zeros -= 2
        elif '1' in pair:
            (pal[k], pal[-k - 1]) = ('1', '1')
            ones -= 2
    if len(pal) % 2 == 1:
        if pal[len(pal) // 2] == '?':
            if zeros % 2 == 1:
                pal[len(pal) // 2] = '0'
            else:
                pal[len(pal) // 2] = '1'
        if pal[len(pal) // 2] == '0':
            zeros -= 1
        elif pal[len(pal) // 2] == '1':
            ones -= 1
    if zeros < 0 or ones < 0 or zeros % 2 == 1:
        return -1
    for k in range(len(pal) // 2):
        pair = [pal[k], pal[-k - 1]]
        if '?' in pair and zeros > 0:
            (pal[k], pal[-k - 1]) = ('0', '0')
            zeros -= 2
        elif '?' in pair:
            (pal[k], pal[-k - 1]) = ('1', '1')
            ones -= 2
    return ''.join(pal)
