def solve(st, a, b):
    li = list(st)
    n = a + b
    for i in range(len(li) // 2):
        if li[i] != li[n - i - 1]:
            if li[i] == '0' and li[n - i - 1] == '1':
                return -1
            if li[i] == '1' and li[n - i - 1] == '0':
                return -1
            if li[i] == '?':
                li[i] = li[n - i - 1]
            else:
                li[n - i - 1] = li[i]
    C = Counter(li)
    (c0, c1) = (C['0'], C['1'])
    if c0 > a or c1 > b:
        return -1
    for i in range(len(li) // 2):
        if li[i] == '?':
            if c0 + 2 <= a:
                li[i] = li[n - i - 1] = '0'
                c0 += 2
            elif c1 + 2 <= b:
                li[i] = li[n - i - 1] = '1'
                c1 += 2
            else:
                return -1
    if n % 2:
        if li[n // 2] == '?':
            if c0 + 1 <= a:
                c0 += 1
                li[n // 2] = '0'
            elif c1 + 1 <= b:
                c1 += 1
                li[n // 2] = '1'
            else:
                return -1
    return ''.join(li)
