def getPalindrome(s, countMap):
    (l, r) = (0, len(s) - 1)
    isPossible = True
    while l <= r:
        if s[l] != s[r]:
            if s[l] == '?':
                s[l] = s[r]
                countMap[s[r]] -= 1
            elif s[r] == '?':
                s[r] = s[l]
                countMap[s[l]] -= 1
            else:
                isPossible = False
                break
        l += 1
        r -= 1
    (l, r) = (0, len(s) - 1)
    while l <= r:
        c = 1 if l == r else 2
        if s[l] == '?' and s[r] == '?':
            if countMap['0'] >= c:
                s[l] = s[r] = '0'
                countMap['0'] -= c
            elif countMap['1'] >= c:
                s[l] = s[r] = '1'
                countMap['1'] -= c
            else:
                isPossible = False
                break
        l += 1
        r -= 1
    if not isPossible or countMap['0'] != 0 or countMap['1'] != 0:
        return -1
    return ''.join(s)
