def commonPrefixes(n, array):
    m = max(max(array), 1)
    ans = ['a' * m]
    for x in range(n):
        e = ans[x][:array[x]]
        if array[x] < m:
            if ans[x][array[x]] == 'a':
                c = 'b'
            else:
                c = 'a'
            e += c * (m - array[x])
        ans.append(e)
    return ans
