(N, K) = map(int, input().split())
X = set([int(input().replace('S', '0000100').replace('E', '0001000').replace('T', '0010000'), 2) for _ in range(N)])
m = int('0011100' * K, 2)
ans = 0
while X:
    x = X.pop()
    for y in X:
        if m - (x | y) ^ ((x & y) >> 2) * 31 & m in X:
            ans += 1
print(ans // 2)

