X = [int(input().replace('S', '0000100').replace('E', '0001000').replace('T', '0010000'), 2) for _ in range(N)]
D = {x: 1 for x in X}
m = int('0011100' * K, 2)
ans = 0
for i in range(N):
    for j in range(i + 1, N):
        if m - (X[i] | X[j]) ^ ((X[i] & X[j]) >> 2) * 31 & m in D:
            ans += 1
