import math
for t in range(int(input())):
    n = int(input())
    a = list(map(int, input().strip().split()))
    if n == 1:
        print(n)
    else:
        ans = 0
        for i in range(len(a) - 1):
            a[i] = abs(a[i] - a[i + 1])
        a.pop()
        (g, st, cur) = (0, 0, 0)
        for i in range(len(a)):
            g = math.gcd(g, a[i])
            cur += 1
            if g != 1:
                ans = max(ans, cur)
            else:
                (g, cur) = (0, 0)
                for j in range(i - st + 1):
                    ind = i - j
                    g = math.gcd(g, a[ind])
                    if g != 1:
                        cur += 1
                        ans = max(ans, cur)
                    else:
                        st = ind + 1
                        break
        print(ans + 1)

import math
for _ in range(int(input())):
    n = int(input())
    a = [int(x) for x in input().split()]
    b = [1] + [abs(a[i] - a[i - 1]) for i in range(1, n)]
    cur = {1: 0}
    ans = 0
    for i in range(1, n):
        g = b[i]
        d = {}
        d[g] = i
        if g != 1:
            tmp = sorted(list(cur.keys()), reverse=True)
            for j in tmp:
                g = math.gcd(g, j)
                if g not in d:
                    d[g] = cur[j]
                if g == 1:
                    ans = max(ans, i - cur[j])
                    break
        cur = d.copy()
    print(ans + 1)

