import sys
input = sys.stdin.readline

def solve():
    (n, k) = map(int, input().split())
    a = list(map(int, input().split()))
    d = [-1000000] * n
    ans = 1000000
    for i in range(0, n):
        z = -1000000
        v = a[i]
        w = a[i] - i
        if i >= v - 1:
            z = 0
        for j in range(i):
            if a[j] < v and a[j] - j >= w:
                z = max(z, d[j])
        d[i] = z + 1
        if z + 1 >= k:
            ans = min(ans, 1 - w)
    if ans >= 1000000:
        print(-1)
    else:
        print(ans)
for i in range(int(input())):
    solve()

