def f(k, a, b, d):
    return a * (k + 1) - k * (k + 1) * b * d // 2

def main():
    t = int(input())
    for _ in range(t):
        (a, b, c, d) = [int(s) for s in input().split()]
        if a > c * b:
            print(-1)
        else:
            k = a // (b * d)
            print(f(k, a, b, d))
main()

def solve(a, b, c, d):
    if a > b * c:
        return -1
    if c <= d:
        return a
    k = a // b // d + 1
    return k * a - k * (k - 1) // 2 * d * b

def main():
    t = int(input())
    for _ in range(t):
        (a, b, c, d) = map(int, input().split())
        print(solve(a, b, c, d))
main()
