import sys

def load_sys():
    return sys.stdin.readlines()

def load_local():
    with open('input.txt', 'r') as f:
        input = f.readlines()
    return input

def km(n, k, m, B):
    if (n - m) % (k - 1) != 0:
        return 'NO'
    R = [0] * m
    L = [0] * m
    for i in range(m):
        R[i] = n - B[i] - (m - i - 1)
        L[i] = n - m - R[i]
        if R[i] >= k // 2 and L[i] >= k // 2:
            return 'YES'
    return 'NO'
input = load_sys()
idx = 1
N = len(input)
while idx < len(input):
    (n, k, m) = [int(x) for x in input[idx].split()]
    B = [int(x) for x in input[idx + 1].split()]
    idx += 2
    print(km(n, k, m, B))
