n = int(input())
a = [int(val) for val in input().split()]
b = [int(val) for val in input().split()]
s = []
t = []
for i in range(n):
    if a[i] < b[i]:
        s.append((a[i], b[i]))
    elif a[i] > b[i]:
        t.append((b[i], a[i]))
s.sort()
t.sort()
x = []
y = []
for i in range(len(s)):
    if i > 0 and s[i][1] < s[i - 1][1]:
        continue
    x.append(s[i])
for i in range(len(t)):
    if i > 0 and t[i][1] < t[i - 1][1]:
        continue
    y.append(t[i])

def get_overlap(t1, t2):
    x_max = max(t1[0], t2[0])
    y_min = min(t1[1], t2[1])
    return max(y_min - x_max, 0)
j = 0
max_overlap = 0
for i in range(len(x)):
    while j < len(y) and y[j][1] < x[i][1]:
        overlap = get_overlap(x[i], y[j])
        if overlap > max_overlap:
            max_overlap = overlap
        j += 1
    if j < len(y):
        overlap = get_overlap(x[i], y[j])
        if overlap > max_overlap:
            max_overlap = overlap
dist = 0
for i in range(n):
    dist += abs(a[i] - b[i])
dist -= 2 * max_overlap
print(dist)

n = int(input())
lis = []
a = list(map(int, input().split()))
b = list(map(int, input().split()))
allsum = 0
for i in range(n):
    allsum += abs(a[i] - b[i])
ans = 0
lis = []
for i in range(n):
    if a[i] < b[i]:
        lis.append((a[i], 1, b[i]))
    elif a[i] > b[i]:
        lis.append((b[i], 2, a[i]))
lis.sort()
nmax = 0
for (x, ty, y) in lis:
    if ty == 1:
        nmax = max(nmax, y)
    else:
        ans = max(ans, min(y - x, nmax - x))
