(n, k) = map(int, input().split())
s = 0
c = 0
for _ in range(k):
    (a, b) = map(int, input().split())
    s += b
    c += b * a
if s > n:
    print(-1)
elif s < n:
    print(1)
elif abs(n * (n + 1) // 2 - c) % n == 0:
    print(1)
else:
    print(-1)

